import React from 'react';
import homepage from './data';
import image from './img/finally.jpg';
import { Link } from 'react-router-dom';

const Home = () => {
  return (
    <div style={{
      display: 'flex',
      flexDirection: 'column',
      alignItems: 'center',
      padding: '2rem',
      minHeight: '100vh',
      fontFamily: 'Roboto, Arial, sans-serif',
      background: 'linear-gradient(to right, #f0f0f0, #c0c0c0)',  // Gradient background
    }}>
      {/* Hero Section */}
      <div style={{
        position: 'relative',
        width: '100%',
        maxWidth: '1400px',
        height: '600px',
        borderRadius: '20px',
        overflow: 'hidden',
        boxShadow: '0 20px 40px rgba(0,0,0,0.2)',
        marginBottom: '4rem',
      }}>
        <img
          src={image}
          style={{
            width: '100%',
            height: '100%',
            objectFit: 'cover',
            filter: 'brightness(0.7) contrast(1.2) blur(3px)',
            transition: 'filter 0.3s ease',
          }}
          alt='Luxora Background'
        />
        <div style={{
          position: 'absolute',
          top: '50%',
          left: '50%',
          transform: 'translate(-50%, -50%)',
          textAlign: 'center',
          color: 'white',
          zIndex: 1,
        }}>
          <h1 style={{
            fontSize: '4rem',
            fontWeight: '800',
            lineHeight: '1.2',
            textShadow: '2px 2px 4px rgba(0,0,0,0.4)',
            marginBottom: '1rem',
          }}>
            Discover Luxora
          </h1>
          <p style={{
            fontSize: '1.8rem',
            fontWeight: '300',
            marginBottom: '2rem',
          }}>
            Elevate Your Shopping Experience
          </p>
          <Link
            to='/shop'
            style={{
              backgroundColor: '#e63946',
              color: 'white',
              padding: '1.2rem 3rem',
              borderRadius: '40px',
              textDecoration: 'none',
              fontWeight: '600',
              fontSize: '1.5rem',
              boxShadow: '0 5px 15px rgba(230, 57, 70, 0.3)',
              transition: 'background-color 0.3s, transform 0.2s',
              ':hover': {
                backgroundColor: '#d62839',
                transform: 'scale(1.05)',
              },
            }}
          >
            Explore Now
          </Link>
        </div>
      </div>

      {/* Products Section */}
      <div style={{
        width: '100%',
        maxWidth: '1400px',
        marginBottom: '4rem',
        textAlign: 'center',
      }}>
        <h2 style={{
          fontSize: '3rem',
          color: '#333',
          marginBottom: '2rem',
          textTransform: 'uppercase',
          letterSpacing: '1px',
        }}>
          Featured Products
        </h2>
        <div style={{
          display: 'grid',
          gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
          gap: '2rem',
          justifyContent: 'center',
        }}>
          {homepage.map((item, index) => (
            <div
              key={`container-${index}`}
              style={{
                borderRadius: '20px',
                overflow: 'hidden',
                boxShadow: '0 10px 20px rgba(0,0,0,0.1)',
                backgroundColor: 'white',
                transition: 'transform 0.3s, box-shadow 0.3s',
                cursor: 'pointer',
                ':hover': {
                  transform: 'translateY(-10px)',
                  boxShadow: '0 15px 30px rgba(0,0,0,0.2)',
                },
              }}
            >
              <img
                src={item.url}
                alt={item.alt}
                style={{
                  width: '100%',
                  height: '250px',
                  objectFit: 'cover',
                }}
              />
              <div style={{
                padding: '1.5rem',
              }}>
                <h3 style={{
                  fontSize: '2.5rem',
                  color: '#333',
                  marginBottom: '1rem',
                }}>
                  {item.title}
                </h3>
                <p style={{
                  fontSize: '1.4rem',
                  color: '#666',
                  marginBottom: '1.5rem',
                }}>
                  {item.content}
                </p>
                <Link
                  to={item.link}
                  style={{
                    display: 'inline-block',
                    backgroundColor: '#e63946',
                    color: 'white',
                    padding: '1rem 2.5rem',
                    borderRadius: '40px',
                    textDecoration: 'none',
                    fontWeight: '600',
                    fontSize: '1.5rem',
                    boxShadow: '0 5px 15px rgba(230, 57, 70, 0.3)',
                    transition: 'background-color 0.3s, transform 0.2s',
                    ':hover': {
                      backgroundColor: '#d62839',
                      transform: 'scale(1.05)',
                    },
                  }}
                >
                  Shop Now
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Footer Section */}
      <footer style={{
        textAlign: 'center',
        padding: '2rem 0',
        fontSize: '1.2rem',
        color: '#666',
        background: '#f0f0f0',
        borderTop: '1px solid #ccc',
        width: '100%',
      }}>
        <p>
          &copy; 2024 Luxora. All rights reserved. | Designed by <Link to='https://github.com/sivaprasath2004' style={{ color: '#e63946', fontWeight: '600' }}>sivaprasath2004</Link>
        </p>
      </footer>
    </div>
  );
};

export default Home;


