import React from "react";
import homepage from "./data";
import image from "./img/finally.jpg";
import { Link } from "react-router-dom";

const Home = () => {
  return (
    <div
      style={{
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
        padding: "2rem",
        minHeight: "100vh",
        fontFamily: "Poppins, Arial, sans-serif",
        background: "linear-gradient(to right, #2e86de, #4a69bd)", // Updated gradient background
      }}
    >
      {/* Hero Section */}
      <div
        style={{
          position: "relative",
          width: "100%",
          maxWidth: "1200px",
          height: "600px",
          borderRadius: "20px",
          overflow: "hidden",
          boxShadow: "0 10px 30px rgba(0,0,0,0.1)",
          marginBottom: "4rem",
          transformStyle: "preserve-3d", // For 3D effects
        }}
      >
        <img
          src={image}
          style={{
            width: "100%",
            height: "100%",
            objectFit: "cover",
            filter: "brightness(0.8) contrast(1.2) blur(3px)",
            transition: "filter 0.3s ease",
            transform: "translateZ(-50px)", // Example of 3D transformation
          }}
          alt="Luxora Background"
        />
        <div
          style={{
            position: "absolute",
            top: "50%",
            left: "50%",
            transform: "translate(-50%, -50%)",
            textAlign: "center",
            color: "white",
            zIndex: 1,
          }}
        >
          <h1
            style={{
              fontSize: "3.5rem",
              fontWeight: "700",
              lineHeight: "1.2",
              textShadow: "2px 2px 4px rgba(0,0,0,0.4)",
              marginBottom: "1rem",
            }}
          >
            Discover Luxora
          </h1>
          <p
            style={{
              fontSize: "1.5rem",
              fontWeight: "300",
              marginBottom: "2rem",
            }}
          >
            Elevate Your Shopping Experience
          </p>
          <Link
            to="/shop"
            style={{
              backgroundColor: "#fdcb6e",
              color: "white",
              padding: "1rem 2.5rem",
              borderRadius: "30px",
              textDecoration: "none",
              fontWeight: "600",
              fontSize: "1.2rem",
              transition: "background-color 0.3s",
              boxShadow: "0 5px 15px rgba(253, 203, 110, 0.4)", // Example of box shadow
              transform: "translateY(0)",
              display: "inline-block",
              ":hover": {
                backgroundColor: "#e17055",
                transform: "translateY(-5px)", // Example of hover effect
                boxShadow: "0 8px 20px rgba(231, 112, 85, 0.4)", // Example of hover shadow
              },
            }}
          >
            Shop Now
          </Link>
        </div>
      </div>

      {/* Products Section */}
      <div
        style={{
          width: "100%",
          maxWidth: "1200px",
          marginBottom: "4rem",
          textAlign: "center",
        }}
      >
        <h2
          style={{
            fontSize: "2.8rem",
            color: "#333",
            marginBottom: "2rem",
            textTransform: "uppercase",
            letterSpacing: "1px",
          }}
        >
          Featured Products
        </h2>
        <div
          style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fit, minmax(300px, 1fr))",
            gap: "2rem",
            justifyContent: "center",
          }}
        >
          {homepage.map((item, index) => (
            <div
              key={`container-${index}`}
              style={{
                borderRadius: "20px",
                overflow: "hidden",
                boxShadow: "0 10px 20px rgba(0,0,0,0.1)",
                backgroundColor: "white",
                transition: "transform 0.3s, box-shadow 0.3s",
                cursor: "pointer",
                ":hover": {
                  transform: "translateY(-10px)",
                  boxShadow: "0 15px 30px rgba(0,0,0,0.2)",
                },
              }}
            >
              <img
                src={item.url}
                alt={item.alt}
                style={{
                  width: "100%",
                  height: "250px",
                  objectFit: "cover",
                }}
              />
              <div
                style={{
                  padding: "1.5rem",
                }}
              >
                <h3
                  style={{
                    fontSize: "2rem",
                    color: "#333",
                    marginBottom: "1rem",
                  }}
                >
                  {item.title}
                </h3>
                <p
                  style={{
                    fontSize: "1.2rem",
                    color: "#666",
                    marginBottom: "1.5rem",
                  }}
                >
                  {item.content}
                </p>
                <Link
                  to={item.link}
                  style={{
                    display: "inline-block",
                    backgroundColor: "#fdcb6e",
                    color: "white",
                    padding: "0.8rem 2rem",
                    borderRadius: "30px",
                    textDecoration: "none",
                    fontWeight: "600",
                    fontSize: "1.2rem",
                    transition: "background-color 0.3s",
                    boxShadow: "0 5px 15px rgba(253, 203, 110, 0.4)", // Example of box shadow
                    ":hover": {
                      backgroundColor: "#e17055",
                      boxShadow: "0 8px 20px rgba(231, 112, 85, 0.4)", // Example of hover shadow
                    },
                  }}
                >
                  Shop Now
                </Link>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Footer Section */}
      <footer
        style={{
          textAlign: "center",
          padding: "2rem 0",
          fontSize: "1rem",
          color: "#666",
          background: "#f0f0f0",
          borderTop: "1px solid #ccc",
          width: "100%",
        }}
      >
        <p>
          &copy; 2024 Luxora. All rights reserved. | Designed by{" "}
          <Link
            to="https://github.com/sivaprasath2004"
            style={{ color: "#fdcb6e", fontWeight: "600" }}
          >
            sivaprasath2004
          </Link>
        </p>
      </footer>
    </div>
  );
};

export default Home;

