import React from 'react';
import homepage from './data';
import { Link } from 'react-router-dom';
import { motion } from 'framer-motion'; // Import for animations

const Home = () => {
  return (
    <div className="home-container">
      {/* Hero Section */}
      <motion.div
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1 }}
        className="hero"
      >
        <div className="hero-image-wrapper">
          <motion.img
            src="path/to/your/image.jpg" // Replace with your hero image path
            alt="Luxora Background"
            className="hero-image"
            style={{ filter: 'brightness(0.8)' }} // Adjust brightness if needed
          />
          <div className="hero-overlay">
            <motion.h1
              initial={{ y: -50 }}
              animate={{ y: 0 }}
              transition={{ duration: 1 }}
              className="hero-title"
            >
              Discover a New Shopping Experience
            </motion.h1>
          </div>
        </div>
        <svg className="hero-wave" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 1440 320">
          <path
            fill="#e63946" // Adjust wave color to match your theme
            d="M0 160L48 144C96 128 192 112 288 128C384 144 480 160 576 152C672 144 768 136 864 160C960 184 1056 208 1152 192C1248 176 1344 160 1440 144L1440 0L0 0Z"
          />
        </svg>
      </motion.div>

      {/* Products Section */}
      <section className="products">
        <h2 className="products-title">
          Products & <span style={{ color: '#e63946' }}>Details</span>
        </h2>
        <p className="products-description">
          Explore our exclusive collection and find your favorites.
        </p>
        <div className="products-grid">
          {homepage.map((item, index) => (
            <motion.div
              key={`container-${index}`}
              className="product-card"
              whileHover={{ scale: 1.03 }}
              transition={{ duration: 0.3 }}
            >
              <motion.img
                src={item.url}
                alt={item.alt}
                className="product-image"
              />
              <div className="product-info">
                <p className="product-content">{item.content}</p>
                <Link
                  to={item.alt}
                  className="product-link"
                >
                  <span>Shop Now</span>
                  <img
                    src="https://cdn-icons-png.flaticon.com/128/8213/8213451.png"
                    alt="Arrow Icon"
                    className="product-arrow"
                  />
                </Link>
              </div>
            </motion.div>
          ))}
        </div>
      </section>

      {/* Footer Section */}
      <footer className="footer">
        <p>
          &copy; 2024{' '}
          <Link to="https://github.com/sivaprasath2004" className="footer-link">
            sivaprasath2004
          </Link>
        </p>
      </footer>
    </div>
  );
};

export default Home;


