import React, { useState, useEffect } from "react";
import axios from "axios";
import eye from "./img/eye.png";
import hide from "./img/hide.png";
import alert_img from "./img/alert.png";
import { useNavigate } from "react-router-dom";
import "./Login.css"; // Import a separate CSS file for styles

const Login = () => {
  const [message, setMessage] = useState("");
  const navigate = useNavigate();
  const [redirect, setRedirect] = useState(false);
  const [loader, setLoader] = useState(false);
  const [passChange, setPassChange] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const setDataToServer = async (e) => {
    e.preventDefault();
    setMessage("");
    setLoader(true);
    try {
      const post = await axios.post(
        "https://shopping-backend-neon.vercel.app/login",
        {
          email,
          password,
        }
      );
      setMessage(post.data);
      if (
        post.data !== "Email Incorrect" &&
        post.data !== "Email or password is not match"
      ) {
        localStorage.setItem("9ouenbcvgetywMhIOEJD", post.data);
        setRedirect(true);
      }
    } catch (error) {
      setMessage("Something went wrong, please try again.");
    } finally {
      setLoader(false);
    }
  };

  useEffect(() => {
    if (redirect) {
      navigate("/");
    }
  }, [redirect, navigate]);

  const handleClick = () => {
    setPassChange(!passChange);
  };

  const passType = passChange ? "text" : "password";
  const passImg = passChange ? hide : eye;

  return (
    <main className="login-container">
      <div className="login-box">
        {loader && (
          <div className="loader-overlay">
            <div className="loader"></div>
            <p className="loading-text">Authenticating...</p>
          </div>
        )}
        <form onSubmit={setDataToServer} className="login-form">
          <h2 className="login-title">Login to Luxora</h2>
          <div className="input-box">
            <input
              type="email"
              required
              name="email"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              className="input-field"
            />
            <label className="input-label">Email</label>
          </div>
          <div className="input-box">
            <span className="icon" onClick={handleClick}>
              <img
                src={passImg}
                alt="toggle password visibility"
                className="password-icon"
              />
            </span>
            <input
              type={passType}
              required
              name="password"
              id="enterpassword"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              className="input-field"
            />
            <label className="input-label">Enter Password</label>
          </div>
          <div className="remember-forget">
            <label className="checkbox-label">
              <input type="checkbox" className="checkbox-input" />
              <span>Remember Me</span>
            </label>
            <a href="/signup" className="forgot-password">
              Forget Password?
            </a>
          </div>
          {message && (
            <div className="error-message">
              <img src={alert_img} alt="error" className="alert-icon" />
              <p className="error-text">{message}</p>
            </div>
          )}
          <button type="submit" className="login-button">
            Login
          </button>
          <div className="register-link">
            <p className="register-text">
              Don't have an account?
              <a href="/signup" className="register-link-text">
                {" "}
                Register
              </a>
            </p>
          </div>
        </form>
      </div>
    </main>
  );
};

export default Login;



